import { Component } from '@angular/core';

@Component({
  selector: 'app-hereos-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})
export class HeroComponent {
 public name: string = 'ironman'
 public age:  number = 45

 get capitalizeName():string {
  return this.name.toUpperCase();
 }

 getHeroDescription():string {
  return ` ${ this.name } - ${ this.age }`;
 }

 changeHero():void {
   this.name = 'Spiderman'
 }

 changeAge(): void {
   this.age = 25
 }

}
<h1>{{name}}</h1>

<dl>
  <td>Nombre:</td>
  <dd>{{name}}</dd>

  <td>Edad:</td>
  <dd>{{age}}</dd>

  <td>Metodo:</td>
  <dd>{{getHeroDescription()}}</dd>

  <td>Capitalizado:</td>
  <dd>{{capitalizeName}}</dd>

</dl>
<button
 *ngIf="name !== 'Spiderman'"
(click)="changeHero()"
class="btn btn-primary mx-2">cambiar Nombre
</button>

<button
 (click)="changeAge()"
  class="btn btn-primary">cambiar Edad
</button>
