import { Component, Input, OnInit } from '@angular/core';
import { Character } from '../../interfaces/character.interface';

@Component({
  selector: 'dbz-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent{

  @Input()
  public CharacterList: Character[]= []


}
<h4>Listado de Personajes</h4>
<ul class="list-group">
 <li *ngFor="let character of CharacterList; let i = index"
   class="list-group-item list-group-item-success">

   <span class="text-primary">{{ i + 1 }}.  </span>

   <span>{{character.name}} - </span>

   <strong>Power: </strong>

   <span>{{character.power}}</span>

   <span> Es el primero: {{i === 0}}</span>

   <span> Es el último: {{i === CharacterList.length -1 }} </span>

  </li>
 </ul>
