import { Component } from '@angular/core';

@Component({
  selector: 'app-hereos-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

public heroNames: string[]= ['Spiderman','IronMan','Hulk','She Hulk','Thor'];
public deleteHero?: string;

removeLastHero():void{
  this.deleteHero = this.heroNames.pop();
  // console.log ({deleteHero})
}

}

<h3>Listado de heroes</h3>

<div *ngIf="deleteHero; else nothingWasDeleted">
  <h3>heroe borrado <small class="text-danger">{{deleteHero}}</small></h3>
</div>


<ng-template #nothingWasDeleted>
  <h3 >No ha borrado nada.</h3>
</ng-template>





<button
*ngIf="heroNames.length > 0"
(click)="removeLastHero()"
  class="btn btn-outline-danger">
  Borrar útimo Heroe
</button>


<ul class="mt-2 list-group">
   <li *ngFor="let name of heroNames"
      class="list-group-item">
      {{name}}
    </li>
</ul>
